var iframe=document.getElementById("youtube-player");if(typeof(iframe)!=='undefined'&&iframe!==null){var tag=document.createElement('script');tag.src="//www.youtube.com/player_api";var firstScriptTag=document.getElementsByTagName('script')[0];firstScriptTag.parentNode.insertBefore(tag,firstScriptTag);var player;function onYouTubePlayerAPIReady(){player=new YT.Player('youtube-player',{events:{'onReady':onPlayerReady}});}
function onPlayerReady(event){var playButton=document.getElementById("play-button");var thumbnail=document.getElementById("thumbnail");playButton.addEventListener('mouseup',function(){thumbnail.classList.add('hidden');iframe.style.zIndex=2;player.playVideo();});}};